import React from "react";
import SpeedComponent from "./SpeedComponent";
import MotorComponent from "./MotorComponent";
import { generateMotorCommands } from "../util/fakeData";
import {useState, useEffect} from "react";
import { generateSpeedData } from "../util/fakeData";

const Dashboard = () =>{
    const[speeds, setSpeeds] = useState(generateSpeedData());
    const[motorCommands, setMotorCommands] = useState(generateMotorCommands());

    useEffect(() => {
        const interval = setInterval(() => {
                setSpeeds(generateSpeedData());
                setMotorCommands(generateMotorCommands());
            }, 1000);

    return () => clearInterval(interval);

    },[]);

    return(    
    <div className='dashboard-container'>
        <header className='dasboard-header'>
            <h1>RACE DASBOARD</h1>
        </header>

    {/* Speed Card Start */}
        <main className='dashboard-content'>
            <SpeedComponent current={speeds.current} showOnlyCurrent={true}/>
        <div className='dashboard-container'>
    {/* Speed Card End */}
    </div>
        <div className='dasboard-container'>
            <div className='dashboard-content'>
                <MotorComponent breaking={motorCommands.breaking} cruise={motorCommands.cruise} manual={motorCommands.manual} 
                throttlePedal={motorCommands.throttlePedal} brakePedal={motorCommands.brakePedal} throttle={motorCommands.throttle} motorRPM={motorCommands.motorRPM}/>
            </div>
        </div>
    </main>
    </div>)
}

export default Dashboard;
