export const generateSpeedData = () => {
  const max = Math.floor(Math.random() * 50) + 30; // 30–80
  const min = Math.floor(Math.random() * 30);      // 0–30
  const average = Math.floor((max + min) / 2);                 // halfway between
  const current = average + Math.floor(Math.random() * 5); // small variation

  return { max, min, average, current };
};


export const generateMotorData = () => {
  // Boolean values for mode states
  const breaking = Math.random() > 0.7; // 30% chance of braking
  const cruise = Math.random() > 0.5;   // 50% chance
  const manual = !cruise;                // opposite of cruise

  // Pedal positions (0-100%) - declare these first before using them
  let brakePedal;
  let throttlePedal;
  let motorRPM;
  let throttle;

  if (breaking) {
    brakePedal = Math.floor(Math.random() * 60) + 40;  // 40-100% when braking
    throttlePedal = 0;                                  // 0% when braking
    motorRPM = Math.floor(Math.random() * 2000) + 1000; // 1000-3000 when braking
    throttle = 0;                                       // 0% when braking
  } else {
    brakePedal = Math.floor(Math.random() * 10);        // 0-10% when not braking
    throttlePedal = Math.floor(Math.random() * 80) + 20; // 20-100% when not braking
    motorRPM = Math.floor(Math.random() * 4000) + 2000;  // 2000-6000 when not braking
    throttle = Math.floor(Math.random() * 70) + 30;      // 30-100% when not braking
  }

  return {
    breaking,
    manual,
    cruise,
    throttle,
    throttlePedal,
    brakePedal,
    motorRPM
  };
};
